import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function Page3() {
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [symptoms, setSymptoms] = useState('');
  const [drug, setDrug] = useState('');

  const handleAgeChange = (event) => {
    setAge(event.target.value);
  }

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  }

  const handleSymptomsChange = (event) => {
    setSymptoms(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    // Perform some logic to determine the suggested drug based on the selected factors
    let suggestedDrug = '';

    if (age === 'under18' && symptoms === 'headache') {
      suggestedDrug = 'Tylenol';
    } else if (age === 'over18' && gender === 'female' && symptoms === 'nausea') {
      suggestedDrug = 'Pepto Bismol';
    } else if (age === 'over18' && gender === 'male' && symptoms === 'backpain') {
      suggestedDrug = 'Ibuprofen';
    } else {
      suggestedDrug = 'Please consult a doctor for further assistance.';
    }

    setDrug(suggestedDrug);
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center">Suggest a Drug</h1>
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="col">
            <label className="form-label">
              Age:
              <select className="form-select" value={age} onChange={handleAgeChange}>
                <option value="">Select an option</option>
                <option value="under18">Under 18</option>
                <option value="over18">Over 18</option>
              </select>
            </label>
          </div>
          <div className="col">
            <label className="form-label">
              Gender:
              <select className="form-select" value={gender} onChange={handleGenderChange}>
                <option value="">Select an option</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
              </select>
            </label>
          </div>
          <div className="col">
            <label className="form-label">
              Symptoms:
              <select className="form-select" value={symptoms} onChange={handleSymptomsChange}>
                <option value="">Select an option</option>
                <option value="headache">Headache</option>
                <option value="nausea">Nausea</option>
                <option value="backpain">Back Pain</option>
              </select>
            </label>
          </div>
          <div className="col-auto">
            <button type="submit" className="btn btn-primary">Submit</button>
          </div>
        </div>
      </form>
      <div className="drug-suggestion mt-5">
        {drug && <p className="text-center">Suggested Drug: {drug}</p>}
      </div>
    </div>
  );
}

export default Page3;
