import React from "react";
import PropTypes from "prop-types";

const shapes = {
  RoundedBorder6: "rounded-radius6",
  CircleBorder19: "rounded-radius19",
  icbRoundedBorder22: "rounded-radius225",
};
const variants = {
  FillWhiteA70014: "bg-white_A700_14 text-white_A700",
  FillOrangeA201: "bg-orange_A201 text-white_A700",
  icbFillLightgreen700: "bg-light_green_700",
};
const sizes = {
  sm: "p-[10px] sm:p-[5px] md:p-[7px]",
  smIcn: "p-[10px] sm:p-[5px] md:p-[7px]",
};

const Button = ({
  children,
  className = "",
  leftIcon,
  rightIcon,
  shape,
  variant,
  size,
  ...restProps
}) => {
  return (
    <button
      className={`${className} ${shapes[shape] || ""} ${
        variants[variant] || ""
      } ${sizes[size] || ""} `}
      {...restProps}
    >
      {!!leftIcon && leftIcon}
      {children}
      {!!rightIcon && rightIcon}
    </button>
  );
};

Button.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node,
  shape: PropTypes.oneOf([
    "RoundedBorder6",
    "CircleBorder19",
    "icbRoundedBorder22",
  ]),
  variant: PropTypes.oneOf([
    "FillWhiteA70014",
    "FillOrangeA201",
    "icbFillLightgreen700",
  ]),
  size: PropTypes.oneOf(["sm", "smIcn"]),
};
Button.defaultProps = {
  className: "",
  shape: "RoundedBorder6",
  variant: "FillWhiteA70014",
  size: "sm",
};

export { Button };
