import React from "react";
import { MDBContainer, MDBIcon } from "mdbreact";
import "./suggestion.css"
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import { useState } from "react";
//import SelectItem from './selectlist';
import Axios from 'axios';
import { useEffect } from "react";
import { Typeahead } from 'react-bootstrap-typeahead';
import options from './data';
import 'react-bootstrap-typeahead/css/Typeahead.css';

import SearchBar from './Components/SearchBar.jsx'
import Users from './data.json'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';

const Suggestion = () => {
  const [searchinput, setSearchinput] = useState('');
  const [saveinput, setSaveinput] = useState([]);
  const [result, setResult] = useState([]);
  //var index = 0;
  const [index, setIndex] = useState(0);

  const [formData, setFormData] = useState({
    dname: '',
    age: '',
    sex: '',
    pregnancy: '',
    lactation: '',
    disease: '',
    kidney: '',
  });


  const handleSubmit = (event) => {
    event.preventDefault(); // prevent default form submission
    console.log(formData); // display form data in console
    console.log(formData.dname.length);
    // handle form submission logic here
    findSuggestion();
  }




  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value }); // update form data
  }

  const handleCheck = (event) => {
    //setIsChecked(event.target.checked);
    //console.log("check");
    //console.log(event.target.checked);
    //console.log(event.target.name);

    //const { name, value } = event.target.checked;
    setFormData({ ...formData, [event.target.name]: event.target.checked });
    // handle checkbox form logic here
  }

  function handleDeleteClick(id,text) {
    const removeItem = saveinput.filter((saveinput) => {
      return saveinput.id !== id
    })
    //console.log(id);
    const removeResult = result.filter((result) => {
      //return (result.drug1 !== text && result.drug2 !== text)
      return (result.id1 !== id && result.id2 !== id)
    })
    console.log(text);
    console.log(result.drug2);
    console.log(saveinput.id);
    console.log(removeItem);
    console.log(removeResult);
    setSaveinput(removeItem);
    setResult(removeResult);
  }

  const objectArrayToArray = (arr, prop) => arr.map(item => item[prop]);

  function getAllPairs(arr) {
    return arr.reduce((result, current, i) => {
        return result.concat(arr.slice(i + 1).map(x => [current, x]))
    }, [])
  }


  function startInput(){
    //console.log(index);
    setIndex(index + 1);
    setSaveinput([
      ...saveinput,
      {
        id: index,
        text: searchinput.trim()
      }
    ])
    setSearchinput('');
    //console.log(saveinput.length);
    //console.log(saveinput);
  }





  function findInteraction(drug1,drug2,id1,id2) {
    Axios.post('http://localhost:3001/interaction', {
      'drug1' : drug1,
      'drug2' : drug2
    }).then((respond) => {
      //console.log(respond.data);
      //console.log(respond.data.map(obj => obj.drug1));

      setResult([
        ...result,
        {
          id: result.length + 1,
          id1: id1,
          id2: id2,
          drug1: respond.data.map(obj => obj.drug1),
          drug2: respond.data.map(obj => obj.drug2),
          level: respond.data.map(obj => obj.level),
          description: respond.data.map(obj => obj.description),
        }
      ])

      //console.log(result);
    })
  }

  function findSuggestion() {
    console.log("active");
    Axios.post('http://localhost:3001/drugdisease', {
      'dname': formData.dname,
      'age': formData.age,
      'sex': formData.sex,
      'pregnancy': formData.pregnancy,
      'lactation': formData.lactation,
      'disease': formData.disease,
      'kidney': formData.kidney,
    }).then((respond) => {
      console.log(respond.data);
    }).catch((error) => {
      console.error(error);
    });
  }



  useEffect(() => {
    if(formData.dname.length > 0){
      //findSuggestion();
    }
    if(saveinput.length >= 2){
      //obj -> array -> 
      //console.log(saveinput.length);
      //console.log(saveinput);
      //console.log(objectArrayToArray(saveinput,'text'));
      let pair = getAllPairs(objectArrayToArray(saveinput,'text'));
      let pair_id = getAllPairs(objectArrayToArray(saveinput,'id'));
      console.log(pair);
      for(var i = 0; i < pair.length; i++){
        //console.log(pair[i]);
        //findInteraction
        findInteraction(pair[i][0],pair[i][1],pair_id[i][0],pair_id[i][1]);
      }

      //console.log(pair[0][0]);
      //console.log(pair[0].length);
      //findInteraction
      //findInteraction(pair[0][0],pair[0][1]);
    }
  }, [saveinput])

  //console.log(saveinput);
  //findInteraction("Abacavir","Aceclofenac");


  //test();
  //test2("Abacavir","Aceclofenac");

  return (
    <MDBContainer>
      <div>
        <br /><h1 className="text-center">Drug Suggestion</h1><br />
      </div>
       {/*Input Field*/}
       {/* 
      <div className="input-group md-form form-sm form-2 pl-0">

        <input className="form-control my-0 py-1 red-border" type="text" placeholder="Search" aria-label="Search" onChange={handleChange} value={searchinput} onKeyPress={event => {
                if (event.key === 'Enter') {
                  if(searchinput !==  ""){
                    startInput();
                  }
                }
              }}/>
        <div className="input-group-append">
          <span className="input-group-text green lighten-3" id="basic-text1">
            <MDBIcon icon="search" className="text-grey" />
          </span>
        </div>
      </div>
      */}
      {/*Form*/}
      <MDBContainer>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="dname">
            <Form.Label>Drug name</Form.Label>
            <Form.Control type="text" placeholder="" name="dname" value={formData.dname} onChange={handleChange}/>
            {/*
            <Form.Text className="text-muted">
            We'll never share your email with anyone else.
            </Form.Text>
            */}
          <div>
            <br /><h6 className="text-left">Factor</h6>
          </div>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Age</Form.Label>
            <Form.Control type="text" placeholder="" name="age" value={formData.age} onChange={handleChange}/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formGridState">
            <Form.Label>Sex</Form.Label>
            <Form.Select  name="sex" value={formData.sex} onChange={handleChange}>
              <option value=''>Choose...</option>
              <option value='male'>male</option>
              <option value='female'>female</option>
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Check inline type="checkbox" label="pregnancy" name="pregnancy" checked={formData.pregnancy} onChange={handleCheck}/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Check inline type="checkbox" label="lactation" name="lactation" checked={formData.lactation} onChange={handleCheck}/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Underlying disease</Form.Label>
            <Form.Control type="text" placeholder="" name="disease" value={formData.disease} onChange={handleChange}/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Kidney function</Form.Label>
            <Form.Control type="text" placeholder="" name="kidney" value={formData.kidney} onChange={handleChange}/>
          </Form.Group>

          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </MDBContainer>



      <div className="output">

        {result.map((result) => (
          <div key={result.id} className='result'>
          <div className="drug1">{result.drug1}</div>
          <div className="drug2">{result.drug2}</div>
          <div className="status">{result.level}</div>
          <div className="describtion">{result.description}</div>
          </div>
        ))}

      </div>
    </MDBContainer>
  );
}
export default Suggestion;
