import { MDBContainer, MDBIcon } from "mdbreact";
import "./suggestion.css"
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import { useState } from "react";
//import SelectItem from './selectlist';
import Axios from 'axios';
import { useEffect } from "react";
import { Typeahead } from 'react-bootstrap-typeahead';
import options from './data';
import 'react-bootstrap-typeahead/css/Typeahead.css';

import SearchBar from './Components/SearchBar.jsx'
import Users from './data.json'
//import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Col from 'react-bootstrap/Col';
import * as React from 'react';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';

import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
} from 'mdb-react-ui-kit';

const Suggestion = () => {
  const [searchinput, setSearchinput] = useState('');
  const [saveinput, setSaveinput] = useState([]);
  const [result, setResult] = useState([]);
  const [sresult, setsResult] = useState('');
  //var index = 0;
  const [index, setIndex] = useState(0);

  const [formData, setFormData] = useState({
    dname: '',
    age: '',
    sex: '',
    pregnancy: '',
    lactation: '',
    disease: '',
    kidney: '',
    weight: '',
  });


  const [open, setOpen] = React.useState(false);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down('md'));

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [basicModal, setBasicModal] = useState(false);

  const toggleShow = () => setBasicModal(!basicModal);


  const handleSubmit = (event) => {
    event.preventDefault(); // prevent default form submission
    console.log(formData); // display form data in console
    console.log(formData.dname.length);
    //check sex
    if(formData.sex == 'male'){
      setFormData({ ...formData, pregnancy: false }); // update form data
      setFormData({ ...formData, lactation: false }); // update form data
    }
    // handle form submission logic here
    findSuggestion();
  }




  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value }); // update form data
  }

  const handleCheck = (event) => {
    //setIsChecked(event.target.checked);
    //console.log("check");
    //console.log(event.target.checked);
    //console.log(event.target.name);

    //const { name, value } = event.target.checked;
    setFormData({ ...formData, [event.target.name]: event.target.checked });
    // handle checkbox form logic here
  }

  function handleDeleteClick(id,text) {
    const removeItem = saveinput.filter((saveinput) => {
      return saveinput.id !== id
    })
    //console.log(id);
    const removeResult = result.filter((result) => {
      //return (result.drug1 !== text && result.drug2 !== text)
      return (result.id1 !== id && result.id2 !== id)
    })
    console.log(text);
    console.log(result.drug2);
    console.log(saveinput.id);
    console.log(removeItem);
    console.log(removeResult);
    setSaveinput(removeItem);
    setResult(removeResult);
  }

  const objectArrayToArray = (arr, prop) => arr.map(item => item[prop]);

  function getAllPairs(arr) {
    return arr.reduce((result, current, i) => {
        return result.concat(arr.slice(i + 1).map(x => [current, x]))
    }, [])
  }


  function startInput(){
    //console.log(index);
