import React from "react";
import { MDBContainer, MDBIcon } from "mdbreact";
import "./interaction.css"
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-css-only/css/bootstrap.min.css';
import 'mdbreact/dist/css/mdb.css';
import { useState } from "react";
import SelectItem from './selectlist';

const Interaction = () => {
  const [searchinput, setSearchinput] = useState('');
  const [saveinput, setSaveinput] = useState([]);

  const handleChange = event => {
    setSearchinput(event.target.value);

  };

  function handleDeleteClick(id) {
    const removeItem = saveinput.filter((saveinput) => {
      return saveinput.id !== id
    })

    setSaveinput(removeItem);
  }

  //console.log(saveinput);

  return (
    <MDBContainer>

      <div className="input-group md-form form-sm form-2 pl-0">
        <input className="form-control my-0 py-1 red-border" type="text" placeholder="Search" aria-label="Search" onChange={handleChange} value={searchinput} onKeyPress={event => {
                if (event.key === 'Enter') {
                  if(searchinput !==  ""){
                    setSaveinput([
                      ...saveinput,
                      {
                        id: saveinput.length + 1,
                        text: searchinput.trim()
                      }
                    ])
                    setSearchinput('');

                  }


                }
              }}/>
        <div className="input-group-append">
          <span className="input-group-text green lighten-3" id="basic-text1">
            <MDBIcon icon="search" className="text-grey" />
          </span>
        </div>
      </div>
      <ul className="select-list">
        {saveinput.map((saveinput) => (
          /*<SelectItem 
            saveinput={saveinput}
            onDeleteClick={handleDeleteClick}
          />*/
          <li key = {saveinput.id}>
            {saveinput.text}
            {" "}
            <button onClick={() => handleDeleteClick(saveinput.id)}>X</button>
        </li>

        ))}
      </ul>


      <div className="output">

        {saveinput.map((saveinput) => (
          <div key={saveinput.id}>
          <div className="drug1">{saveinput.text}</div>
          <div className="drug2">{saveinput.text}</div>
          <div className="status">{saveinput.text}</div>
          <div className="describtion">{saveinput.text}</div>
          </div>
        ))}

      </div>
    </MDBContainer>
  );
}

export default Interaction;
